package week1.day3;

public class FindEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 6;
		if (num % 2 == 0) {
			System.out.println("The number is even-->" + num);
		} else {
			System.out.println("The number is not a even-->" + num);
		}

	}

}
