package week1.day3;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 7;
		if (num % 2 == 0) {
			System.out.println("The number is a odd-->" + num);
		} else {
			System.out.println("The number is not a odd-->" + num);
		}

	}

}
